mapper.select:
select ibdtb.data_table_id dataTableId, ibdtb.data_model_id dataModelId, ibdtb.data_table_code dataTableCode, ibdtb.data_table_name dataTableName, ibdtb.data_table_remark dataTableRemark, ibdmb.data_model_name dataModelName from ik_bp_data_table_basic ibdtb left join ik_bp_data_model_basic ibdmb on ibdmb.data_model_id = ibdtb.data_model_id where 1=1
AND ibdtb.data_model_id = #{dataModelId}
AND ibdtb.is_del = 0 and ibdmb.is_del = 0

select ibdtb.data_table_id dataTableId, ibdtb.data_model_id dataModelId, ibdtb.data_table_code dataTableCode, ibdtb.data_table_name dataTableName, ibdtb.data_table_remark dataTableRemark from ik_bp_data_table_basic ibdtb where 1=1
AND (data_table_name = #{dataTableName} OR data_table_code = #{dataTableName})
AND data_model_id = #{dataModelId}
